/**
 * 判断是否是Number
 * @param s
 */
declare function isNumber(s: any): boolean;
/**
 * 判断是否是String
 * @param s
 */
declare function isString(s: any): boolean;
/**
 * 判断是否是Array
 * @param s
 */
declare function isArray(s: any): boolean;
/**
 * 强转int
 * @param s
 */
declare function int(s: any): number;